INSERT INTO protolytic_referral_history (claim_number, date, referral_info, referral_action_id, row_item_id, referral_action_text, referral_number) 
VALUES (#[message.inboundProperties.'http.query.params'.claim_number], 
NOW(), #[message.inboundProperties.'http.query.params'.referral_info], #[message.inboundProperties.'http.query.params'.referral_action_id], #[message.inboundProperties.'http.query.params'.id], #[message.inboundProperties.'http.query.params'.referral_action_text], #[message.inboundProperties.'http.query.params'.referral_number]);

