db:select.db:parameterized-query:
SELECT DISTINCT concat(claimant_last_name, ', ', claimant_first_name) patient_name, ri.claim_number, ca.adjuster_name adjuster_name
FROM row_item ri, authority_row_item_detection rid, protolytic_claims_assignment ca
WHERE ri.claim_number = ca.claim_number AND ca.date_to > NOW();

